import React, { useState } from 'react';

const Coordinators = () => {
  const [currentCoordinator, setCurrentCoordinator] = useState(0);

  const coordinators = [
    {
      name: 'Rajesh Kumar',
      role: 'Event Coordinator',
      course: 'Computer Science Engineering',
      year: '3rd Year',
      image: '/api/placeholder/300/300',
      linkedin: 'https://linkedin.com/in/rajesh-kumar',
      description: 'Passionate about organizing tech events and fostering innovation in the student community.'
    },
    {
      name: 'Priya Sharma',
      role: 'Technical Lead',
      course: 'Information Technology',
      year: '4th Year',
      image: '/api/placeholder/300/300',
      linkedin: 'https://linkedin.com/in/priya-sharma',
      description: 'Full-stack developer with expertise in modern web technologies and AI/ML applications.'
    },
    {
      name: 'Amit Singh',
      role: 'Logistics Coordinator',
      course: 'Electronics & Communication',
      year: '3rd Year',
      image: '/api/placeholder/300/300',
      linkedin: 'https://linkedin.com/in/amit-singh',
      description: 'Specializes in IoT and embedded systems, with a keen interest in hardware-software integration.'
    },
    {
      name: 'Sneha Patel',
      role: 'Marketing Coordinator',
      course: 'Computer Science Engineering',
      year: '2nd Year',
      image: '/api/placeholder/300/300',
      linkedin: 'https://linkedin.com/in/sneha-patel',
      description: 'Creative mind focused on cybersecurity and digital marketing strategies.'
    },
    {
      name: 'Vikram Gupta',
      role: 'Judging Coordinator',
      course: 'Information Technology',
      year: '4th Year',
      image: '/api/placeholder/300/300',
      linkedin: 'https://linkedin.com/in/vikram-gupta',
      description: 'Experienced in competitive programming and algorithm design with multiple hackathon wins.'
    },
    {
      name: 'Ananya Reddy',
      role: 'Community Manager',
      course: 'Computer Science Engineering',
      year: '3rd Year',
      image: '/api/placeholder/300/300',
      linkedin: 'https://linkedin.com/in/ananya-reddy',
      description: 'Active in open source communities and passionate about building inclusive tech environments.'
    }
  ];

  const nextCoordinator = () => {
    setCurrentCoordinator((prev) => (prev + 1) % coordinators.length);
  };

  const prevCoordinator = () => {
    setCurrentCoordinator((prev) => (prev - 1 + coordinators.length) % coordinators.length);
  };

  return (
    <section id="coordinators" className="section-padding bg-dark-800/30">
      <div className="container-custom">
        <div className="text-center mb-16">
          <h2 className="text-4xl md:text-5xl font-bold mb-6">
            Meet Our <span className="text-gradient">Coordinators</span>
          </h2>
          <p className="text-xl text-gray-400 max-w-3xl mx-auto">
            Our dedicated team of coordinators is working hard to make Aviothic 2.0 an unforgettable experience for all participants.
          </p>
        </div>

        {/* Coordinator Slider */}
        <div className="max-w-4xl mx-auto">
          <div className="card p-0 overflow-hidden">
            <div className="grid grid-cols-1 lg:grid-cols-2 gap-8 p-8">
              {/* Coordinator Image */}
              <div className="relative">
                <img
                  src={coordinators[currentCoordinator].image}
                  alt={`${coordinators[currentCoordinator].name} - ${coordinators[currentCoordinator].role}`}
                  className="w-full h-80 object-cover rounded-xl"
                />
                
                {/* Navigation Arrows */}
                <button
                  onClick={prevCoordinator}
                  className="absolute left-4 top-1/2 transform -translate-y-1/2 bg-dark-900/80 hover:bg-dark-900 text-white p-2 rounded-full transition-all duration-200 hover:scale-110"
                  aria-label="Previous coordinator"
                >
                  <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 19l-7-7 7-7" />
                  </svg>
                </button>
                
                <button
                  onClick={nextCoordinator}
                  className="absolute right-4 top-1/2 transform -translate-y-1/2 bg-dark-900/80 hover:bg-dark-900 text-white p-2 rounded-full transition-all duration-200 hover:scale-110"
                  aria-label="Next coordinator"
                >
                  <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5l7 7-7 7" />
                  </svg>
                </button>
              </div>

              {/* Coordinator Details */}
              <div className="space-y-6">
                <div>
                  <h3 className="text-3xl font-bold text-white mb-2">
                    {coordinators[currentCoordinator].name}
                  </h3>
                  <div className="text-xl text-primary-400 font-semibold mb-2">
                    {coordinators[currentCoordinator].role}
                  </div>
                  <div className="text-gray-400 mb-4">
                    {coordinators[currentCoordinator].course} â€¢ {coordinators[currentCoordinator].year}
                  </div>
                </div>

                <p className="text-gray-300 leading-relaxed">
                  {coordinators[currentCoordinator].description}
                </p>

                <div className="flex space-x-4">
                  <a
                    href={coordinators[currentCoordinator].linkedin}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="btn-secondary flex items-center space-x-2"
                  >
                    <svg className="w-5 h-5" fill="currentColor" viewBox="0 0 24 24">
                      <path d="M20.447 20.452h-3.554v-5.569c0-1.328-.027-3.037-1.852-3.037-1.853 0-2.136 1.445-2.136 2.939v5.667H9.351V9h3.414v1.561h.046c.477-.9 1.637-1.85 3.37-1.85 3.601 0 4.267 2.37 4.267 5.455v6.286zM5.337 7.433c-1.144 0-2.063-.926-2.063-2.065 0-1.138.92-2.063 2.063-2.063 1.14 0 2.064.925 2.064 2.063 0 1.139-.925 2.065-2.064 2.065zm1.782 13.019H3.555V9h3.564v11.452zM22.225 0H1.771C.792 0 0 .774 0 1.729v20.542C0 23.227.792 24 1.771 24h20.451C23.2 24 24 23.227 24 22.271V1.729C24 .774 23.2 0 22.222 0h.003z"/>
                    </svg>
                    <span>Connect</span>
                  </a>
                </div>
              </div>
            </div>
          </div>

          {/* Dots Indicator */}
          <div className="flex justify-center space-x-2 mt-8">
            {coordinators.map((_, index) => (
              <button
                key={index}
                onClick={() => setCurrentCoordinator(index)}
                className={`w-3 h-3 rounded-full transition-all duration-200 ${
                  index === currentCoordinator 
                    ? 'bg-primary-500 scale-125' 
                    : 'bg-gray-600 hover:bg-gray-500'
                }`}
                aria-label={`Go to coordinator ${index + 1}`}
              />
            ))}
          </div>
        </div>

        {/* All Coordinators Grid (Desktop) */}
        <div className="hidden lg:grid grid-cols-2 xl:grid-cols-3 gap-6 mt-16">
          {coordinators.map((coordinator, index) => (
            <div
              key={coordinator.name}
              className={`card group hover:scale-105 transition-all duration-300 cursor-pointer ${
                index === currentCoordinator ? 'ring-2 ring-primary-500' : ''
              }`}
              onClick={() => setCurrentCoordinator(index)}
            >
              <div className="text-center">
                <img
                  src={coordinator.image}
                  alt={`${coordinator.name} - ${coordinator.role}`}
                  className="w-24 h-24 rounded-full mx-auto mb-4 object-cover group-hover:scale-110 transition-transform duration-300"
                />
                <h4 className="text-lg font-semibold text-white mb-1 group-hover:text-primary-400 transition-colors duration-300">
                  {coordinator.name}
                </h4>
                <p className="text-primary-400 text-sm mb-2">{coordinator.role}</p>
                <p className="text-gray-500 text-xs">{coordinator.course}</p>
              </div>
            </div>
          ))}
        </div>

        {/* Contact Coordinators */}
        <div className="text-center mt-16">
          <div className="card max-w-2xl mx-auto">
            <h3 className="text-2xl font-bold text-white mb-4">
              Need Help?
            </h3>
            <p className="text-gray-400 mb-6">
              Our coordinators are here to help you with any questions about the event, 
              registration, or technical requirements.
            </p>
            <a
              href="#contact"
              className="btn-primary"
            >
              Contact Coordinators
            </a>
          </div>
        </div>
      </div>
    </section>
  );
};

export default Coordinators;
