import React, { useState } from 'react';
import themesImage from '../assests/themes.png';

const Themes = () => {
  const [currentSlide, setCurrentSlide] = useState(0);

  const themeSlides = [
    {
      title: 'AI/ML',
      description: 'Build intelligent applications using cutting-edge AI tools and machine learning algorithms',
      icon: 'ðŸ¤–',
      color: 'from-blue-500 to-cyan-500',
      bgColor: 'bg-gradient-to-br from-blue-400 to-cyan-300'
    },
    {
      title: 'IoT',
      description: 'Innovate with connected devices and create smart solutions for the Internet of Things',
      icon: 'ðŸŒ',
      color: 'from-green-500 to-emerald-500',
      bgColor: 'bg-gradient-to-br from-green-400 to-emerald-300'
    },
    {
      title: 'Web Development',
      description: 'Create full-stack applications with modern frameworks and technologies',
      icon: 'ðŸ’»',
      color: 'from-purple-500 to-pink-500',
      bgColor: 'bg-gradient-to-br from-purple-400 to-pink-300'
    },
    {
      title: 'Cyber Security',
      description: 'Solve real-world security challenges and protect digital assets',
      icon: 'ðŸ”’',
      color: 'from-red-500 to-orange-500',
      bgColor: 'bg-gradient-to-br from-red-400 to-orange-300'
    },
    {
      title: 'Embedded Systems',
      description: 'Work on hardware-software integration projects and embedded solutions',
      icon: 'âš¡',
      color: 'from-yellow-500 to-amber-500',
      bgColor: 'bg-gradient-to-br from-yellow-400 to-amber-300'
    }
  ];

  const nextSlide = () => {
    setCurrentSlide((prev) => (prev + 1) % themeSlides.length);
  };

  const prevSlide = () => {
    setCurrentSlide((prev) => (prev - 1 + themeSlides.length) % themeSlides.length);
  };

  return (
    <section 
      id="themes" 
      className="section-padding relative bg-gradient-to-br from-dark-900 via-dark-800 to-dark-900"
    >
      {/* Vibrant Neon Circuit Board Pattern */}
      <div className="absolute inset-0 overflow-hidden">
        {/* Enhanced Grid Pattern */}
        <div className="absolute inset-0 opacity-25" style={{
          backgroundImage: `
            linear-gradient(rgba(34, 211, 238, 0.5) 1px, transparent 1px),
            linear-gradient(90deg, rgba(34, 211, 238, 0.5) 1px, transparent 1px)
          `,
          backgroundSize: '25px 25px'
        }}></div>

        {/* Major Circuit Traces - Bigger and Brighter */}
        {/* Main Horizontal Trace */}
        <div className="absolute top-1/4 left-1/12 w-full h-2 bg-gradient-to-r from-transparent via-cyan-400 to-transparent opacity-80"
             style={{ boxShadow: '0 0 20px rgba(34, 211, 238, 0.8), 0 0 40px rgba(34, 211, 238, 0.4)' }}></div>

        {/* Main Vertical Trace */}
        <div className="absolute top-1/6 right-1/4 w-2 h-full bg-gradient-to-b from-transparent via-green-400 to-transparent opacity-75"
             style={{ boxShadow: '0 0 18px rgba(34, 197, 94, 0.7), 0 0 36px rgba(34, 197, 94, 0.35)' }}></div>

        {/* Diagonal Major Trace */}
        <div className="absolute top-1/3 left-1/6 w-96 h-2 bg-gradient-to-r from-transparent via-magenta-400 to-transparent opacity-70 transform rotate-12"
             style={{ boxShadow: '0 0 16px rgba(236, 72, 153, 0.6), 0 0 32px rgba(236, 72, 153, 0.3)' }}></div>

        {/* Secondary Circuit Lines */}
        <div className="absolute top-1/6 left-1/4 w-64 h-1 bg-cyan-400/90 opacity-70"
             style={{ boxShadow: '0 0 12px rgba(34, 211, 238, 0.6)' }}></div>
        <div className="absolute top-1/6 left-1/4 w-1 h-48 bg-cyan-400/90 opacity-70"
             style={{ boxShadow: '0 0 12px rgba(34, 211, 238, 0.6)' }}></div>

        <div className="absolute bottom-1/4 right-1/3 w-80 h-1 bg-green-400/85 opacity-65"
             style={{ boxShadow: '0 0 14px rgba(34, 197, 94, 0.6)' }}></div>
        <div className="absolute bottom-1/4 right-1/3 w-1 h-56 bg-green-400/85 opacity-65"
             style={{ boxShadow: '0 0 14px rgba(34, 197, 94, 0.6)' }}></div>

        <div className="absolute top-2/3 left-1/8 w-72 h-1 bg-magenta-400/90 opacity-60"
             style={{ boxShadow: '0 0 10px rgba(236, 72, 153, 0.5)' }}></div>
        <div className="absolute top-2/3 left-1/8 w-1 h-40 bg-magenta-400/90 opacity-60"
             style={{ boxShadow: '0 0 10px rgba(236, 72, 153, 0.5)' }}></div>

        {/* Large Geometric Shapes */}
        {/* Big Squares */}
        <div className="absolute top-1/8 left-1/5 w-16 h-16 border-2 border-cyan-400/80 opacity-60"
             style={{ boxShadow: '0 0 20px rgba(34, 211, 238, 0.6)' }}></div>
        <div className="absolute bottom-1/8 right-1/5 w-20 h-20 border-2 border-green-400/75 opacity-55"
             style={{ boxShadow: '0 0 24px rgba(34, 197, 94, 0.6)' }}></div>
        <div className="absolute top-3/4 right-1/8 w-18 h-18 border-2 border-magenta-400/80 opacity-50"
             style={{ boxShadow: '0 0 22px rgba(236, 72, 153, 0.6)' }}></div>

        {/* Filled Large Squares */}
        <div className="absolute top-1/12 left-1/3 w-12 h-12 bg-cyan-400/40 opacity-50"
             style={{ boxShadow: '0 0 16px rgba(34, 211, 238, 0.5)' }}></div>
        <div className="absolute bottom-1/12 right-1/3 w-14 h-14 bg-green-400/45 opacity-45"
             style={{ boxShadow: '0 0 18px rgba(34, 197, 94, 0.5)' }}></div>
        <div className="absolute top-1/2 left-1/8 w-10 h-10 bg-magenta-400/50 opacity-40"
             style={{ boxShadow: '0 0 14px rgba(236, 72, 153, 0.5)' }}></div>

        {/* Large Connection Nodes */}
        <div className="absolute top-1/4 left-1/6 w-8 h-8 bg-cyan-400/90 rounded-full opacity-80 animate-pulse"
             style={{ boxShadow: '0 0 25px rgba(34, 211, 238, 0.8)' }}></div>
        <div className="absolute top-1/3 right-1/4 w-10 h-10 bg-green-400/85 rounded-full opacity-75 animate-pulse"
             style={{ boxShadow: '0 0 28px rgba(34, 197, 94, 0.8)' }}></div>
        <div className="absolute bottom-1/3 left-1/3 w-8 h-8 bg-magenta-400/90 rounded-full opacity-70 animate-pulse"
             style={{ boxShadow: '0 0 25px rgba(236, 72, 153, 0.8)' }}></div>
        <div className="absolute top-2/3 right-1/6 w-6 h-6 bg-cyan-400/95 rounded-full opacity-85 animate-pulse"
             style={{ boxShadow: '0 0 20px rgba(34, 211, 238, 0.7)' }}></div>
        <div className="absolute top-1/5 left-1/4 w-8 h-8 bg-green-400/90 rounded-full opacity-80 animate-pulse"
             style={{ boxShadow: '0 0 25px rgba(34, 197, 94, 0.8)' }}></div>
        <div className="absolute top-1/2 right-1/3 w-10 h-10 bg-magenta-400/85 rounded-full opacity-75 animate-pulse"
             style={{ boxShadow: '0 0 28px rgba(236, 72, 153, 0.8)' }}></div>

        {/* Large Hollow Circles */}
        <div className="absolute top-1/6 left-1/7 w-12 h-12 border-2 border-cyan-400/70 rounded-full opacity-50"
             style={{ boxShadow: '0 0 18px rgba(34, 211, 238, 0.5)' }}></div>
        <div className="absolute bottom-1/6 right-1/7 w-14 h-14 border-2 border-green-400/65 rounded-full opacity-45"
             style={{ boxShadow: '0 0 20px rgba(34, 197, 94, 0.5)' }}></div>
        <div className="absolute top-3/4 left-1/5 w-10 h-10 border-2 border-magenta-400/70 rounded-full opacity-40"
             style={{ boxShadow: '0 0 16px rgba(236, 72, 153, 0.5)' }}></div>

        {/* Data Flow Indicators - Bigger */}
        <div className="absolute top-1/4 left-1/12 w-4 h-4 bg-cyan-400/95 rounded-full opacity-90 animate-ping"
             style={{ boxShadow: '0 0 15px rgba(34, 211, 238, 0.8)' }}></div>
        <div className="absolute top-1/3 right-1/4 w-4 h-4 bg-green-400/95 rounded-full opacity-85 animate-ping"
             style={{ boxShadow: '0 0 15px rgba(34, 197, 94, 0.8)' }}></div>
        <div className="absolute bottom-1/3 left-1/3 w-4 h-4 bg-magenta-400/95 rounded-full opacity-80 animate-ping"
             style={{ boxShadow: '0 0 15px rgba(236, 72, 153, 0.8)' }}></div>

        {/* Digital Text Elements */}
        <div className="absolute top-1/8 left-1/12 text-cyan-400/40 text-sm font-mono opacity-30">1010</div>
        <div className="absolute bottom-1/8 right-1/12 text-green-400/40 text-sm font-mono opacity-30">1101</div>
        <div className="absolute top-3/4 left-1/8 text-magenta-400/40 text-sm font-mono opacity-30">0110</div>
        <div className="absolute top-1/2 left-1/6 text-cyan-400/35 text-lg font-mono opacity-25">L</div>

        {/* Enhanced Power Rails */}
        <div className="absolute top-1/12 left-0 w-full h-1 bg-gradient-to-r from-transparent via-cyan-400/60 to-transparent opacity-50"
             style={{ boxShadow: '0 0 15px rgba(34, 211, 238, 0.5)' }}></div>
        <div className="absolute bottom-1/12 left-0 w-full h-1 bg-gradient-to-r from-transparent via-green-400/60 to-transparent opacity-45"
             style={{ boxShadow: '0 0 15px rgba(34, 197, 94, 0.5)' }}></div>

        {/* Additional Circuit Branches */}
        <div className="absolute top-1/5 left-1/2 w-48 h-1 bg-cyan-400/80 opacity-60"
             style={{ boxShadow: '0 0 12px rgba(34, 211, 238, 0.5)' }}></div>
        <div className="absolute top-1/5 left-1/2 w-1 h-32 bg-cyan-400/80 opacity-60"
             style={{ boxShadow: '0 0 12px rgba(34, 211, 238, 0.5)' }}></div>

        <div className="absolute bottom-1/5 right-1/2 w-56 h-1 bg-green-400/75 opacity-55"
             style={{ boxShadow: '0 0 14px rgba(34, 197, 94, 0.5)' }}></div>
        <div className="absolute bottom-1/5 right-1/2 w-1 h-36 bg-green-400/75 opacity-55"
             style={{ boxShadow: '0 0 14px rgba(34, 197, 94, 0.5)' }}></div>
      </div>
      <div className="container-custom relative z-10">
        <div className="text-center mb-16">
          <div className="mb-12">
            <h1 className="text-2xl sm:text-3xl md:text-4xl lg:text-5xl font-bold text-transparent bg-clip-text bg-gradient-to-b from-yellow-400 via-orange-500 to-red-600 mb-8 block">
              HACKATHON
            </h1>
            <h2 className="text-xl sm:text-2xl md:text-3xl lg:text-4xl font-bold text-transparent bg-clip-text bg-gradient-to-b from-yellow-400 via-orange-500 to-red-600 block">
              THEMES
            </h2>
          </div>
          <p className="text-lg sm:text-xl text-orange-200 max-w-3xl mx-auto mb-8">

            Explore diverse technology domains and showcase your expertise in any area that excites you
          </p>

          {/* Main Theme */}
          <div className="max-w-2xl mx-auto mb-8 sm:mb-12">
            <div className="card text-center bg-gradient-to-r from-primary-600/10 to-primary-500/10 border-primary-500/30 p-4 sm:p-6">
              <div className="text-4xl sm:text-5xl md:text-6xl mb-3 sm:mb-4">ðŸš€</div>
              <h3 className="text-2xl sm:text-3xl font-bold text-transparent bg-clip-text bg-gradient-to-r from-cyan-400 via-blue-400 to-cyan-400 mb-3 sm:mb-4">Open Innovation</h3>
              <p className="text-sm sm:text-base md:text-lg text-gray-300">
                The main theme for Aviothic 2.0 is <strong className="text-white">Open Innovation</strong>. 
                We encourage participants to think outside the box and create solutions 
                that can make a real impact in the world.
              </p>
            </div>
          </div>
        </div>

        {/* Desktop: Image Slider with Overlapping Cards */}
        <div className="hidden lg:block relative max-w-7xl mx-auto mb-16">
          <div className="relative h-[400px] sm:h-[450px] md:h-[500px] flex items-center justify-center">
            {/* Transparent background with neon border */}
            <div className="absolute inset-0 bg-transparent rounded-3xl border-2 border-neon-green/50 backdrop-blur-sm"></div>

            {/* Slider Container */}
            <div className="relative flex items-center justify-center space-x-4 w-full h-full">
              {/* Previous Button */}
              <button
                onClick={prevSlide}
                className="absolute left-2 sm:left-4 z-20 bg-dark-800/60 hover:bg-dark-800/80 text-neon-green border border-neon-green/50 hover:border-neon-green p-2 sm:p-3 rounded-full transition-all duration-200 hover:scale-110 neon-glow-green"
                aria-label="Previous theme"
              >
                <svg className="w-4 h-4 sm:w-6 sm:h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M15 19l-7-7 7-7" />
                </svg>
              </button>

              {/* Next Button */}
              <button
                onClick={nextSlide}
                className="absolute right-2 sm:right-4 z-20 bg-dark-800/60 hover:bg-dark-800/80 text-neon-green border border-neon-green/50 hover:border-neon-green p-2 sm:p-3 rounded-full transition-all duration-200 hover:scale-110 neon-glow-green"
                aria-label="Next theme"
              >
                <svg className="w-4 h-4 sm:w-6 sm:h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M9 5l7 7-7 7" />
                </svg>
              </button>

              {/* Theme Cards with Overlapping Effect */}
              {themeSlides.map((theme, index) => {
                const isActive = index === currentSlide;
                const isPrev = index === (currentSlide - 1 + themeSlides.length) % themeSlides.length;
                const isNext = index === (currentSlide + 1) % themeSlides.length;

                let position = 'hidden';
                if (isActive) position = 'z-30 transform scale-100';
                else if (isPrev) position = 'z-20 transform scale-90 -translate-x-8 sm:-translate-x-12 md:-translate-x-16 opacity-70';
                else if (isNext) position = 'z-20 transform scale-90 translate-x-8 sm:translate-x-12 md:translate-x-16 opacity-70';

                return (
                  <div
                    key={theme.title}
                    className={`absolute transition-all duration-300 ease-out ${position}`}
                  >
                    <div className={`w-80 h-72 sm:w-96 sm:h-80 rounded-2xl border-2 border-neon-green/60 shadow-2xl bg-dark-800/40 backdrop-blur-sm flex flex-col items-center justify-center text-center p-4 sm:p-6 md:p-8`}>
                      {/* Icon */}
                      <div className="text-6xl sm:text-7xl md:text-8xl lg:text-9xl mb-4 sm:mb-6">
                        {theme.icon}
                      </div>

                      {/* Title */}
                      <h3 className="text-2xl sm:text-3xl md:text-4xl font-bold text-white mb-3 sm:mb-4">
                        {theme.title}
                      </h3>

                      {/* Description */}
                      <p className="text-white/90 text-sm sm:text-base leading-relaxed">
                        {theme.description}
                      </p>
                    </div>
                  </div>
                );
              })}
            </div>
          </div>

        </div>

        {/* Mobile/Tablet: Horizontal Slider */}
        <div className="lg:hidden overflow-x-auto blue-neon-scrollbar mb-8">
          <div className="flex space-x-6 snap-x snap-mandatory pb-4">
            {themeSlides.map((theme, index) => (
              <div
                key={theme.title}
                className="min-w-[85%] sm:min-w-[70%] snap-start bg-gradient-to-br from-gray-800/50 to-gray-900/50 backdrop-blur-sm rounded-xl p-6 shadow-xl border border-cyan-500/20 hover:border-cyan-400/40 transition-colors duration-200 group relative"
              >
                {/* Blur overlay for non-active cards */}
                {index !== currentSlide && (
                  <div className="absolute inset-0 bg-black/30 rounded-xl z-10 pointer-events-none"></div>
                )}

                <div className="text-center">
                  {/* Icon */}
                  <div className="text-6xl sm:text-7xl mb-4 sm:mb-6">
                    {theme.icon}
                  </div>

                  {/* Title */}
                  <h3 className="text-2xl sm:text-3xl font-bold text-cyan-100 mb-3 sm:mb-4 group-hover:text-cyan-50 transition-colors duration-300">
                    {theme.title}
                  </h3>

                  {/* Description */}
                  <p className="text-blue-200 text-sm sm:text-base leading-relaxed group-hover:text-blue-100 transition-colors duration-300">
                    {theme.description}
                  </p>
                </div>
              </div>
            ))}
          </div>
        </div>


      </div>
    </section>
  );
};

export default Themes;
