        <div className="absolute top-1/3 left-1/6 w-64 h-64 border border-green-400/20 rounded-full opacity-10"
             style={{
               boxShadow: '0 0 30px rgba(34, 197, 94, 0.1)'
             }}>
        </div>

        {/* Scattered Neon Elements */}
        <div className="absolute top-1/5 left-1/3 w-4 h-4 bg-cyan-400/60 rounded-full opacity-40 animate-pulse"
             style={{
               boxShadow: '0 0 10px rgba(34, 211, 238, 0.4)'
             }}>
        </div>
        <div className="absolute top-2/3 left-1/4 w-3 h-3 bg-green-400/60 rounded-full opacity-50 animate-pulse"
             style={{
               boxShadow: '0 0 8px rgba(34, 197, 94, 0.4)'
             }}>
        </div>
        <div className="absolute top-1/2 right-1/3 w-5 h-5 bg-purple-400/50 rounded-full opacity-35 animate-pulse"
             style={{
               boxShadow: '0 0 12px rgba(168, 85, 247, 0.3)'
             }}>
        </div>
        <div className="absolute bottom-1/4 left-1/2 w-2 h-2 bg-blue-400/70 rounded-full opacity-45 animate-pulse"
             style={{
               boxShadow: '0 0 6px rgba(59, 130, 246, 0.4)'
             }}>
        </div>

        {/* Dashed Lines */}
        <div className="absolute top-1/4 left-1/2 w-32 h-0.5 border-t border-dashed border-cyan-400/30 opacity-40"></div>
        <div className="absolute bottom-1/3 right-1/4 w-24 h-0.5 border-t border-dashed border-green-400/25 opacity-35"></div>
        <div className="absolute top-3/4 left-1/5 w-20 h-0.5 border-t border-dashed border-purple-400/30 opacity-30"></div>

        {/* Grid Pattern Overlay */}
        <div className="absolute inset-0 opacity-10" style={{
          backgroundImage: `radial-gradient(circle at 1px 1px, rgba(34, 211, 238, 0.3) 1px, transparent 0)`,
          backgroundSize: '40px 40px'
        }}></div>

        {/* Additional Geometric Shapes */}
        <div className="absolute top-1/6 right-1/6 w-16 h-16 border border-purple-400/40 transform rotate-12 opacity-25"
             style={{
               boxShadow: '0 0 15px rgba(168, 85, 247, 0.2)'
             }}>
        </div>
        <div className="absolute bottom-1/5 left-1/8 w-12 h-12 border border-blue-400/35 transform -rotate-12 opacity-20"
             style={{
               boxShadow: '0 0 12px rgba(59, 130, 246, 0.15)'
             }}>
        </div>
